import { Component,OnInit} from '@angular/core';
import { AuthService } from '../../services/auth.service';
import { TaskService } from '../../services/tasks.service';

@Component({
  selector: 'app-public-task',
  templateUrl: './public-task.component.html',
  styleUrls: ['./public-task.component.css']
})
export class PublicTaskComponent implements OnInit {
  user: any; // Variable para almacenar los datos del usuario

  constructor(private authService: AuthService, private taskService: TaskService) { }


    ngOnInit(): void {
      const userRole = this.authService.getRole();

      // Verificar si el usuario tiene el rol "cliente" antes de obtener los datos
      if (this.authService.loggedIn() && userRole === 'client') {
        // Obtener los datos del usuario autenticado
        this.taskService.getPrivateTasks().subscribe(
          (res) => {
            this.user = res.user; // Asignar los datos del usuario
          },
          (err) => {
            console.error(err);
          }
        );
      }
    }
  }
